import random

class QuoteDatabase:
    def __init__(self):
        self.quotes = [
            "Believe you can and you're halfway there.",
            "The only way to do great work is to love what you do.",
            "It always seems impossible until it's done."
        ]

    def get_random_quote(self):
        return random.choice(self.quotes)

def add_quote(self, new_quote):
    if new_quote and new_quote not in self.quotes:
        self.quotes.append(new_quote)
        return True
    return False

def add_new_quote(self):
    text = self.new_quote_entry.get()
    if self.db.add_quote(text):
        messagebox.showinfo("Success", "Quote added successfully!")
        self.new_quote_entry.delete(0, tk.END)
    else:
        messagebox.showwarning("Warning", "Please enter a unique quote.")
